package br.ufc.frameworkkotlin.model.mapek.observer

interface IPlanningObservable {
    val observers: MutableList<IPlanningObserver>

    fun add(observer: IPlanningObserver){
        observers.add(observer)
    }

    fun sendUpdateEvent(){
        observers.forEach{ it.update() }
    }
}