import br.ufc.frameworkkotlin.ui.MainActivity
import task.Task2

class LightAction:Task2() {
    override fun recebeToken(o: Any?) {}

    override fun retornaToken(): Any? {
        return null
    }

    override fun executar() {
        MainActivity.flagMonitoring = true
        //MainActivity.texto = "Danger"
        MainActivity.texto = BaseActivity.finalStatus !!
        Log.i("Light status",BaseActivity.finalStatus !! )
    }

    override fun retorno(): Any? {
        return null
    }

    override fun evaluate(o: Any) {
        BaseActivity.finalStatus  = o as String

        ///Begin verification of special Status
        if (BaseActivity.finalStatus !!.contains("Fall") || BaseActivity.finalStatus!!.contains("Bump"))
            Thread(
            this
        ).start()
        ///End verification of special Status

    }

    override fun run() {
        executar()
    }
}